const addElement=function(e,t){var n=document.createElement(e);return n.className=t,n};let menuButton=document.querySelector("[data-menu-toggle]"),menu=document.querySelector("#menu");menuButton.addEventListener("click",()=>{menu.classList.toggle("active")});let menuList=document.querySelector("#menu .nav"),content=document.querySelector("#content");function getContent(e){fetch(e).then(function(e){return e.json()}).then(function(e){createMenu(e),addContent(e[0].content)})}function createMenu(e){e.forEach((e,t)=>{let n=createMenuItem(e.title,e.address);0===t&&n.classList.add("active"),menuList.append(n),n.addEventListener("click",t=>{t.preventDefault(),addContent(e.content),removeItemClasses(menuList),n.classList.add("active")})})}function createMenuItem(e,t){let n=addElement("li","nav-item w-100"),c=addElement("a","nav-link text-truncate");return c.href=t,c.textContent=e,n.append(c),n}function addContent(e){content.innerHTML=e}function removeItemClasses(e){e.querySelectorAll("li").forEach(e=>{e.classList.remove("active")})}getContent("/content.json");